function assignees() {
    $('.assignee-block').find('button.add').click(function() {
        var block = $(this).parents('.assignee-block');
        var id = $(block).find('.user-id').val();
        if( id.length == 0 ) {
            return;
        }
        var btn = $(this);
        var url = $(block).data('add-url');
        var data = {
            _token: $(block).data('token'),
            user_id: id
        };
        btn.button('loading');
        $.post(url, data, function(response) {
            if( !response.success ) {
                showError('Assignee not added', response.message);
            } else {
                $(response.user).appendTo($(block).find('.assignee-list'));
                $(block).find('input').val('');
            }
        }).always(function() {
            btn.button('reset');
        });
    });
    $('.assignee-block').on('click', '.delete-user', function() {
        var block = $(this).parents('.assignee-block');
        var id = $(this).data('id');
        var url = $(block).data('remove-url');
        var user_block = $(this).parents('li');
        var data = {
            _token: $(block).data('token'),
            user_id: id
        };
        var btn = $(this);
        btn.attr('disabled', 'disabled');
        $.post(url, data, function(response) {
            if( !response.success ) {
                showError('Assignee not removed', 'An error occurred');
            } else {
                $(user_block).fadeOut(function() {
                    $(this).remove();
                });
            }
        }).always(function() {
            btn.removeAttr('disabled');
        });
    });
}

