function updateNotes(){
    $(document).on('click focus active', '.note-content[data-contenteditable="true"]', function(e){
        var el = $(this);
        if(el.hasClass('content-editable')) return;
        $('.note-content[data-contenteditable="true"]').not(this).removeClass('content-editable').attr('contenteditable',false);
        el.addClass('content-editable').attr('contenteditable',true).removeClass('note-content-editable').focus();

        var content = el.find('p').text();

        el.on('blur', function(){
            el.removeClass('content-editable').attr('contenteditable',false).addClass('note-content-editable');
            var newContent = el.find('p').text();
            if( content != newContent )
                el.addClass('content-editable-changed');
        })

        var content = el.find('p').text();

        el.on('click', '.update-note', function(e){
            e.preventDefault();
            e.stopPropagation();
            var element = $(this).parents('.note');
            // console.log(['element', element]);
            var href = $(element).attr('data-update-url');
            var token = $(element).parents('#notes-list-holder').attr('data-token');
            // console.log(['parents', $(element).parents('#notes-list-holder')]);

            swal({
                title: 'Update note',
                text: 'Are you sure you want to update note!',
                type: "info",
                showCancelButton: true,
                confirmButtonColor: "#5cb85c",
                confirmButtonText: "Update",
                closeOnConfirm: false
            }, function(){
                $.post(href, { _token: token, content: content }, function() {
                    swal("Updated!", 'Note updated!', "success");
                    el.removeClass('content-editable-changed');
                    // $(element).fadeOut(function() { $(this).remove(); });
                }).fail(function() {
                    swal("An error occurred", "Please check your connection and try again.", "error");
                });
            });
        });

    });
}


