function ajaxFormsSubmit(){

    $('body').on('click','form.ajax button', function(e) {
        e.preventDefault();
        e.stopPropagation();



        var $this = $(this).parents('form.ajax');
        var button = $(this);

        $this.find('button').removeClass('active');
        button.addClass('active');

        var response_to = $($this.attr('data-response-selector'));
        var name = button.attr('name') || null;
        var value = button.attr('value') || null;
        var token = $this.attr('data-token');
        var extra_data = 'ajax=true&_token='+token+'&';
        if(name != null){
            extra_data +=name+'='+value+'&';
        }

        response_to.html('<i class="fa fa-spinner fa-spin" style="width: 100%; height: 100px; line-height: 100px; text-align: center; font-size: 30px" aria-hidden="true"></i>')

        button.button('loading');
        var form_data = $this.serialize();
        data = extra_data+form_data;
        data = JSON.parse('{"' + decodeURI(data.replace(/&/g, "\",\"").replace(/=/g,"\":\"")) + '"}');
        console.log(data);
        $.ajax({
            url: $this.attr('action'),
            method: $this.attr('method'),
            data: data
        }).done(function (data, status, xhr) {
            button.button('reset');
            console.log(data.html);
            response_to.html(data.html)
            // location.reload();
        });
    });
}
